// Extract and reformat the nested payload
const originalPayload = $input.all()[0];
const slackEvent = originalPayload.json.body[""];

// Create clean new structure
return {
  headers: originalPayload.json.headers,
  slack: {
    user: slackEvent.user,
    channel: slackEvent.channel,
    text: slackEvent.text.replace(/<@U.*?>/, "").trim(),
    team: slackEvent.team,
    timestamp: slackEvent.event_ts,
    is_thread: !!slackEvent.thread_ts,
    thread_id: slackEvent.thread_ts || "main"
  }
};
