// Validate incoming webhook data
const data = $input.first().json.body;
const errors = [];

// Check required fields
if (!data.event_type) errors.push("Missing event_type");
if (!data.call_id) errors.push("Missing call_id");
if (!data.call_data) errors.push("Missing call_data");
if (!data.messages || !Array.isArray(data.messages)) errors.push("Missing or invalid messages array");

// Validate call_data structure
if (data.call_data) {
  if (!data.call_data.userName) errors.push("Missing userName in call_data");
  if (!data.call_data.startedAt) errors.push("Missing startedAt timestamp");
  if (!data.call_data.endedAt) errors.push("Missing endedAt timestamp");
}

// If there are errors, throw with details
if (errors.length > 0) {
  throw new Error("Validation failed: " + errors.join(", "));
}

// Add calculated fields
const startTime = new Date(data.call_data.startedAt);
const endTime = new Date(data.call_data.endedAt);
const durationMs = endTime - startTime;
const durationMinutes = durationMs / 60000;

// Return enriched data
return [{
  json: {
    body: data,
    validation: {
      isValid: true,
      timestamp: new Date().toISOString()
    },
    calculated: {
      durationMinutes: durationMinutes.toFixed(2),
      durationFormatted: formatDuration(durationMs),
      dayOfWeek: startTime.toLocaleDateString('en-US', { weekday: 'long' }),
      timeOfDay: getTimeOfDay(startTime),
      messageCount: data.messages.length,
      hasActionItems: checkForActionItems(data.messages)
    }
  }
}];

// Helper functions
function formatDuration(ms) {
  const seconds = Math.floor(ms / 1000);
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}m ${remainingSeconds}s`;
}

function getTimeOfDay(date) {
  const hour = date.getHours();
  if (hour < 6) return "Early Morning";
  if (hour < 12) return "Morning";
  if (hour < 17) return "Afternoon";
  if (hour < 21) return "Evening";
  return "Night";
}

function checkForActionItems(messages) {
  const actionKeywords = ['will do', 'action item', 'follow up', 'next step', 'todo', 'task'];
  return messages.some(msg => 
    actionKeywords.some(keyword => 
      msg.message.toLowerCase().includes(keyword)
    )
  );
}

