// Get the content string from the OpenAI response
const content = $input.first().json.message.content;

// Use a regular expression to extract the JSON inside the code block
// This matches content between ```json and ``` or just between ``` and ```
const match = content.match(/```(?:json)?\s*([\s\S]*?)```/);

if (!match || !match[1]) {
  // If no match with code block, try to extract JSON directly from the content
  // Sometimes the response might have JSON without code blocks
  const directJsonMatch = content.match(/\{[\s\S]*\}/);
  if (directJsonMatch) {
    try {
      return [{ json: JSON.parse(directJsonMatch[0]) }];
    } catch (e) {
      throw new Error('Found potential JSON but failed to parse it: ' + e.message);
    }
  }
  throw new Error('No JSON code block found in content.');
}

// Clean up the JSON string
let jsonString = match[1]
  .replace(/\/\/.*$/gm, '')     // Remove single-line comments
  .replace(/\/\*[\s\S]*?\*\//g, '') // Remove multi-line comments
  .replace(/,(\s*[}\]])/g, '$1') // Remove trailing commas
  .trim();                       // Trim whitespace

let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (e) {
  // If initial parse fails, try a more aggressive cleaning approach
  try {
    // Try to fix common JSON errors
    jsonString = jsonString
      .replace(/(['"])?([a-zA-Z0-9_]+)(['"])?\s*:/g, '"$2":') // Ensure property names are quoted
      .replace(/:\s*'([^']*)'/g, ':"$1"');                   // Replace single quotes with double quotes

    parsed = JSON.parse(jsonString);
  } catch (fallbackError) {
    throw new Error('Failed to parse JSON: ' + e.message + 
      '\nExtracted string:\n' + jsonString);
  }
}

return [{ json: parsed }];
