// Generate the interview system prompt
const jobDescription = $input.first().json.jobDescription;

const systemPrompt = `You are a professional HR interviewer conducting job interviews. Your goal is to assess candidates thoroughly while making them feel comfortable and valued.

Job Description:
${jobDescription}

Interview Approach:
- Start with a warm welcome and brief introduction
- Ask about their interest in the role and company
- Explore their relevant experience with specific examples
- Assess technical skills and cultural fit
- Allow time for their questions
- Close professionally with next steps

Be conversational, ask follow-up questions, and show genuine interest in their responses. If they seem nervous, help them feel at ease. Always maintain a professional yet friendly tone.`;

const welcomeMessage = `Hello! Welcome to our interview. I'm excited to learn about your background and discuss this opportunity with you. 

Let's start with you telling me a bit about yourself and what interests you about this position.`;

return [{
  json: {
    jobDescription,
    systemPrompt,
    welcomeMessage,
    agentName: `HR Interview Bot - ${new Date().toLocaleDateString()}`
  }
}];

const workflowStaticData = $getWorkflowStaticData('global');



const agentId = $input.first().json.id;
const interviewLink = $input.first().json.call_link;
const jobDescription = $('Prepare Interview Agent').first().json.jobDescription;

workflowStaticData.agentId = agentId;
workflowStaticData.jobDescription = jobDescription;
workflowStaticData.interviewLink = interviewLink;



return [{
  json: {
    "Agent ID": workflowStaticData.agentId,
    "Agent Name":$('Prepare Interview Agent').first().json.agentName,
    "Interview Link": interviewLink,
    "Job Description": workflowStaticData.jobDescription,
    "System Prompt": $('Prepare Interview Agent').first().json.systemPrompt,
    "Welcome Message": $('Prepare Interview Agent').first().json.welcomeMessage

  }
}];

// Parse AI analysis
const analysisText = $input.first().json.message.content;
const analysis = JSON.parse(analysisText.match(/```json\n([\s\S]*?)\n```/)[1]);
const interviewData = $input.first().json;

// Format for Google Sheets
return [{
  json: {
    "Candidate Name": $('Is Our Interview?').first().json.webhookData.call_data.userName,
    "Interview Date":  $now,
    "Duration (minutes)": $('Is Our Interview?').first().json.webhookData.evaluation.duration_minutes,
    "Fit Score": analysis.fitScore,
    "Recommendation": analysis.recommendation,
    "Strengths": analysis.strengths.join("; "),
    "Concerns": analysis.concerns.join("; ") || "None",
    "Summary": analysis.summary,
    "Next Steps": analysis.nextSteps.join("; "),
    "Call ID": $('Is Our Interview?').first().json.webhookData.call_id,
    "Analyzed On": $now,
  }
}];

const workflowStaticData = $getWorkflowStaticData('global');
const MY_AGENT_ID = workflowStaticData.agentId;

const jobDescription = workflowStaticData.jobDescription;

const webhookData = $input.first().json.body;
const receivedAgentId = webhookData.agent_id || webhookData.agentId || 
                        (webhookData.call_data && webhookData.call_data.agent_id);

// Only process if it's our agent and the call ended
if (receivedAgentId === MY_AGENT_ID && webhookData.event_type === "call_ended") {
  return [{
    json: {
      process: true,
      jobDescription,
      webhookData
    }
  }];
} else {
  return [{
    json: {
      process: false,
      reason: receivedAgentId !== MY_AGENT_ID ? 
        "Interview from different agent" : 
        "Call not finished yet"
    }
  }];
}
