// Define section titles in the order your items arrive
const sectionTitles = [
  "MBA News",
  "Product Management",
  "AI Automation News"
];

// Combine and format each message with a section header
const formattedSections = items.map((item, idx) => {
  const title = sectionTitles[idx] || `Section ${idx + 1}`;
  const content = item.json.message || '';

  // Apply your formatting, showing the actual URL as the link text
  const formattedContent = content
    .replace(/\n/g, '<br>')
    .replace(/## (.*?)<br>/g, '<h2 style="color: #2563eb; margin: 25px 0 15px 0; border-bottom: 2px solid #e5e7eb; padding-bottom: 8px;">$1</h2>')
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    .replace(/- \*\*(.*?)\*\*:/g, '<div style="margin: 15px 0;"><strong style="color: #1e40af;">â€¢ $1:</strong>')
    .replace(/\[\d+\]/g, '') // Remove citation numbers for cleaner display
    .replace(/(https?:\/\/[^\s<]+)/g, '<a href="$1" style="color: #2563eb; text-decoration: none;">$1</a>');

  return `
    <section style="margin-bottom: 40px;">
      <h2 style="color: #2563eb; margin: 25px 0 15px 0; border-bottom: 2px solid #e5e7eb; padding-bottom: 8px;">${title}</h2>
      <div>${formattedContent}</div>
    </section>
  `;
});

// Combine all sections into a single message string
return [{
  json: {
    message: formattedSections.join('')
  }
}];
