// === FUNKCJE POMOCNICZE ===

function safeArrayItem(arr, index) {
  return Array.isArray(arr) && arr.length > index ? arr[index] : null;
}

function getPageSpeedMetric(result, metricName) {
  try {
    const variations = [
      metricName,
      metricName.replace('-', ' '),
      metricName.replace(/-/g, '_')
    ];

    for (const variant of variations) {
      if (result?.audits?.[variant]?.displayValue) {
        return result.audits[variant].displayValue;
      }
    }
    return 'N/A';
  } catch (e) {
    return 'N/A';
  }
}

function generateHeadersTable(headers) {
  if (!Array.isArray(headers) || headers.length === 0) return '<tr><td colspan="2">N/A</td></tr>';
  return headers.map((h, i) => `
    <tr>
      <td>${i + 1}</td>
      <td>${h}</td>
    </tr>
  `).join('');
}

function generateLinksTable(title, linksArray) {
  if (!Array.isArray(linksArray) || linksArray.length === 0) {
    return `<h4>${title} (0)</h4><p style="color:red;">No found invalid links.</p>`;
  }

  const rows = linksArray.map((link, index) => `
    <tr>
      <td>${index + 1}</td>
      <td><a href="${link}" target="_blank" rel="noopener noreferrer">${link}</a></td>
    </tr>
  `).join('');

  return `
    <h4>${title} (${linksArray.length})</h4>
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; width: 100%; margin-bottom: 20px;">
      <thead style="background-color: #f2f2f2;">
        <tr>
          <th>#</th>
          <th>URL</th>
        </tr>
      </thead>
      <tbody>
        ${rows}
      </tbody>
    </table>
  `;
}


function generateSocialMediaTable(socialLinks) {
  const platforms = [
    { name: 'Facebook', key: 'facebook' },
    { name: 'Instagram', key: 'instagram' },
    { name: 'Twitter', key: 'twitter' },
    { name: 'LinkedIn', key: 'linkedin' }
  ];

  const rows = platforms.map(platform => {
    const rawValue = socialLinks?.[platform.key];
    const value = rawValue === true || rawValue === 'Yes' ? 'Yes' : 'No';
    const cssClass = value === 'Yes' ? 'badge-yes' : 'badge-no';

    return `
      <tr>
        <th>${platform.name}</th>
        <td class="${cssClass}">${value}</td>
      </tr>
    `;
  }).join('');

  return `
    <h2>Social Media Links Presence</h2>
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; width: 100%; margin-bottom: 20px;">
      <tbody>
        ${rows}
      </tbody>
    </table>
  `;
}

function generateTechnicalSeoTable(flags) {
  const checks = [
    { label: 'Robots.txt found', value: flags.robotstxt },
    { label: 'Sitemap.xml found', value: flags.sitemaxml },
    { label: 'SSL Certificate (HTTPS)', value: flags.httpscheck },
    { label: 'Mobile Friendly', value: flags.mobilefriendly },
    { label: 'Friendly URLs (no query params)', value: flags.friendlyurl },
    { label: 'Meta tag noindex present', value: flags.noindex},
    { label: 'Google Analytics present', value: flags.gaanalytics }
  ];

  return `
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; width: 100%; margin-bottom: 20px;">
      <thead style="background-color: #f2f2f2;">
        <tr>
          <th>Check</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        ${checks.map(check => {
          const isYes = check.invert ? check.value !== 'Yes' : check.value === 'Yes';
          const status = isYes ? 'Yes' : 'No';
          const cssClass = isYes ? 'badge-yes' : 'badge-no';
          return `
            <tr>
              <td>${check.label}</td>
              <td class="${cssClass}">${status}</td>
            </tr>
          `;
        }).join('')}
      </tbody>
    </table>
  `;
}

function generateImagesTable(images, alts) {
  if (!Array.isArray(images) || images.length === 0) {
    return '<tr><td colspan="3">No images found</td></tr>';
  }
  return images.map((src, i) => {
    const altText = alts && alts[i] ? alts[i] : '<span style="color:red">Brak alt</span>';
    return `
      <tr>
        <td><img src="${src}" alt="${altText}" style="max-width:100px; max-height:80px;"/></td>
        <td><a href="${src}" target="_blank" rel="noopener noreferrer">${src}</a></td>
        <td>${altText}</td>
      </tr>
    `;
  }).join('');
}

function getRating(score, perfect = 100) {
  const percentage = (score / perfect) * 100;
  if (percentage >= 90) return '⭐️⭐️⭐️⭐️⭐️';
  if (percentage >= 70) return '⭐️⭐️⭐️⭐️';
  if (percentage >= 50) return '⭐️⭐️⭐️';
  if (percentage >= 30) return '⭐️⭐️';
  return '⭐️';
}

function getColorIndicator(value, type = 'score') {
  let numericValue = parseFloat(value);
  if (isNaN(numericValue)) numericValue = 0;

  if (type === 'score') {
    if (numericValue >= 0.9) return `<span style="color: #4CAF50; font-weight: bold;">${value}</span>`;
    if (numericValue >= 0.7) return `<span style="color: #8BC34A;">${value}</span>`;
    if (numericValue >= 0.5) return `<span style="color: #FFC107;">${value}</span>`;
    return `<span style="color: #F44336; font-weight: bold;">${value}</span>`;
  } else if (type === 'time') {
    if (numericValue <= 1.5) return `<span style="color: #4CAF50; font-weight: bold;">${value}</span>`;
    if (numericValue <= 3) return `<span style="color: #8BC34A;">${value}</span>`;
    if (numericValue <= 5) return `<span style="color: #FFC107;">${value}</span>`;
    return `<span style="color: #F44336; font-weight: bold;">${value}</span>`;
  }
  return value;
}

