// 1. Funkcja do tworzenia URL z fallbackiem
function createUrl(href, base) {
  try {
    // Próbuj użyć natywnego URL jeśli dostępny
    return new URL(href, base);
  } catch (e) {
    // Fallback dla środowisk bez pełnego wsparcia URL
    try {
      const baseObj = parseBaseUrl(base);
      const resolved = resolveUrl(href, baseObj);
      return {
        href: resolved,
        hostname: new RegExp(/https?:\/\/([^\/]+)/).exec(resolved)[1],
        protocol: resolved.startsWith('https') ? 'https:' : 'http:'
      };
    } catch (err) {
      throw new Error(`Invalid URL: ${href}`);
    }
  }
}

// 2. Pomocnicze funkcje do parsowania URL
function parseBaseUrl(base) {
  const match = base.match(/^(https?:\/\/[^\/]+)/);
  if (!match) throw new Error(`Invalid base URL: ${base}`);
  return {
    protocol: match[1].split('://')[0] + ':',
    host: match[1].split('://')[1],
    origin: match[1]
  };
}

function resolveUrl(href, baseObj) {
  if (href.startsWith('http')) return href;
  if (href.startsWith('//')) return baseObj.protocol + href;
  if (href.startsWith('/')) return baseObj.origin + href;
  return baseObj.origin + '/' + href;
}


// Główna logika
const baseUrlString = $('Webhook').first().json.query.url;
const baseUrl = createUrl(baseUrlString, baseUrlString);

// 1. Pobieranie linków z większą elastycznością
let links = [];

// Sprawdź różne możliwe formaty danych wejściowych
if (Array.isArray($input.first().json.links)) {
  links = $input.first().json.links;
} else if ($input.first().json.links && typeof $input.first().json.links === 'object') {
  // Jeśli links jest obiektem, konwertuj do tablicy
  links = Object.values($input.first().json.links);
} else {
  // Jeśli nie ma danych, użyj pustej tablicy
  links = [];
}

// 2. Filtruj puste wartości
links = links.filter(link => {
  if (typeof link === 'string' && link.trim() !== '') return true;
  if (typeof link?.href === 'string' && link.href.trim() !== '') return true;
  return false;
});

// 3. Analiza linków
const result = { 
  internalLinks: [], 
  externalLinks: [], 
  invalidLinks: [],
  counts: {
    internal: 0,
    external: 0,
    invalid: 0,
    total: links.length
  }
};

for (const href of links) {
  try {
    // Obsłuż zarówno stringi jak i obiekty z polami href
    const linkUrl = typeof href === 'string' ? href : href.href;
    const url = createUrl(linkUrl, baseUrl.href);

    const normalize = host => host.replace('www.', '').toLowerCase();

    if (normalize(url.hostname) === normalize(baseUrl.hostname)) {
      result.internalLinks.push(url.href);
      result.counts.internal++;
    } else {
      result.externalLinks.push(url.href);
      result.counts.external++;
    }
  } catch (e) {
    const invalidLink = typeof href === 'string' ? href : href.href;
    result.invalidLinks.push(invalidLink);
    result.counts.invalid++;
  }
}

// 4. Zwróć wyniki
return [{
  json: {
    ...result,
    counts: {
      internal: result.internalLinks.length,
      external: result.externalLinks.length,
      invalid: result.invalidLinks.length,
      total: links.length
    }
  }
}];

