// 1. Pobierz dane z poprzedniego węzła
const keyword = $('Webhook').first().json.query.keyword;  // Bez {{ }} - bezpośredni dostęp do $json
const body = $('HTTP Request - Get Page').first().json.data;              // Bez {{ }} - $json zawiera już dane

// 2. Sprawdź, czy keyword i body istnieją
if (!keyword || !body) {
    throw new Error("Brak wymaganych danych: 'keyword' lub 'body'");
}

// 3. Przygotuj tekst do analizy (usuń HTML i znaki specjalne)
const cleanBody = body
    .toString()
    .replace(/<[^>]*>/g, ' ')     // Usuń tagi HTML
    .replace(/[^\w\s]/g, ' ')      // Usuń znaki specjalne (zostaw tylko litery, cyfry i spacje)
    .replace(/\s+/g, ' ')          // Zamień wiele spacji na jedną
    .toLowerCase();

const keywordLower = keyword.toString().toLowerCase();

// 4. Oblicz statystyki
const keywordCount = (cleanBody.match(new RegExp(keywordLower, 'gi')) || []).length;
const totalWords = cleanBody.split(' ').filter(word => word.trim().length > 0).length;
const density = totalWords > 0 ? ((keywordCount / totalWords) * 100).toFixed(2) + '%' : '0%';

// 5. Zwróć wynik
return [{
    keyword: keywordLower,
    keywordCount,
    totalWords,
    keywordDensity: density,
    isOptimal: parseFloat(density) >= 1.0 && parseFloat(density) <= 3.0,
    sampleText: cleanBody.slice(0, 100) + (cleanBody.length > 100 ? "..." : "")
}];

