// Funkcja do wyodrębniania domeny głównej
function getRootDomain(url) {
  if (!url) return null;

  try {
    // Dodaj protokół jeśli brakuje
    const urlWithProtocol = url.includes('://') ? url : `https://${url}`;
    const parsedUrl = new URL(urlWithProtocol);

    // Usuń subdomeny (www też) i zwróć czystą domenę
    const domainParts = parsedUrl.hostname.split('.');
    const isLocalhost = domainParts.includes('localhost');

    if (isLocalhost || domainParts.length <= 2) {
      return `${parsedUrl.protocol}//${parsedUrl.hostname}`;
    }

    // Dla normalnych domen bierzemy ostatnie 2 części
    const rootDomain = domainParts.slice(-2).join('.');
    return `${parsedUrl.protocol}//${rootDomain}`;
  } catch (error) {
    // Ręczna ekstrakcja dla niepoprawnych URLi
    const domainMatch = url.match(
      /^(https?:\/\/)?(www\.)?([a-z0-9-]+\.[a-z]{2,})(\/|$)/i
    );
    return domainMatch ? `https://${domainMatch[3]}` : null;
  }
}

// Pobierz URL z inputu
const inputUrl = $input.first().json?.query?.url || $input.first().json?.domain || '';

// Wygeneruj główną domenę
const rootDomain = getRootDomain(inputUrl);

// Przygotuj wynik
const result = {
  originalUrl: inputUrl,
  rootDomain: rootDomain
};

return [{
  json: result
}];

const url = $('Webhook').first().json.query.url;
