const getOrigin = (fullUrl) => {
  const match = fullUrl.match(/^(https?:\/\/[^\/?#]+)/i);
  return match ? match[1] : fullUrl;
};

const origin = getOrigin(url);

async function checkFile(path) {
  try {
    const res = await axios.get(`${origin}/${path}`);
    return res.status === 200 ? "Yes" : "No";
  } catch {
    return "No";
  }
}

const isHttps = origin.startsWith('https') ? "Yes" : "No";
const hasQueryParams = url.includes('?') ? "No" : "Yes";

const results = {
  robotsTxt: await checkFile('robots.txt'),
  sitemapXml: await checkFile('sitemap.xml'),
  https: isHttps,
  mobileFriendly: html.includes('viewport') ? "Yes" : "No",
  friendlyUrls: hasQueryParams,
  noIndex: html.includes('noindex') ? "Yes" : "No",

  googleAnalytics: (
    html.includes('gtag(') ||
    html.includes('G-') ||
    html.includes('UA-')
  ) ? "Yes" : "No",

