// Analyze a single post to understand Polylang structure
const results = {
  discovery: [],
  polylangFields: [],
  languageHandling: '',
  recommendations: [],
  rawData: {}
};

// Get the post data
const items = $input.all();
if (items.length === 0) {
  return {
    error: 'No input data received',
    recommendation: 'Check if the previous nodes executed successfully'
  };
}

const postData = items[0].json;
results.rawData.sample = postData;

// Look for language fields in the post
if (postData) {
  for (const key in postData) {
    if (key.toLowerCase().includes('lang') || 
        key.toLowerCase().includes('locale') ||
        key.toLowerCase().includes('translation')) {
      results.polylangFields.push({
        field: key,
        value: postData[key],
        type: typeof postData[key]
      });
    }
  }

  // Check _links for language info
  if (postData._links) {
    for (const rel in postData._links) {
      if (rel.includes('translation') || 
          rel.includes('language') ||
          rel.includes('pll')) {
        results.polylangFields.push({
          field: `_links.${rel}`,
          value: postData._links[rel],
          type: 'link relation'
        });
      }
    }
  }

  // Check _embedded for language data
  if (postData._embedded) {
    for (const embed in postData._embedded) {
      if (embed.includes('term') || embed.includes('language')) {
        results.discovery.push({
          type: 'embedded',
          field: embed,
          data: postData._embedded[embed]
        });
      }
    }
  }

  // Check meta fields
  if (postData.meta) {
    for (const metaKey in postData.meta) {
      if (metaKey.toLowerCase().includes('lang') || 
          metaKey.toLowerCase().includes('locale')) {
        results.polylangFields.push({
          field: `meta.${metaKey}`,
          value: postData.meta[metaKey],
          type: 'meta field'
        });
      }
    }
  }
}

// Generate recommendations
if (results.polylangFields.length > 0) {
  results.languageHandling = 'Polylang fields detected in REST API';
  results.recommendations.push('Language fields found: ' + results.polylangFields.map(f => f.field).join(', '));
  results.recommendations.push('Use these fields when creating posts');
} else {
  results.languageHandling = 'No obvious Polylang fields in post data';
  results.recommendations.push('Language might be handled through custom taxonomies or separate API calls');
  results.recommendations.push('Check the taxonomies and routes endpoints for more information');
}

return results;

// Analyze taxonomies for language handling
const results = {
  languageTaxonomies: [],
  allTaxonomies: [],
  recommendations: []
};

const items = $input.all();
if (items.length === 0 || !items[0].json) {
  return { error: 'No taxonomy data received' };
}

const taxonomies = items[0].json;

// List all taxonomies
for (const taxSlug in taxonomies) {
  const tax = taxonomies[taxSlug];
  results.allTaxonomies.push({
    slug: taxSlug,
    name: tax.name,
    restBase: tax.rest_base
  });

  // Check for language-related taxonomies
  if (taxSlug.includes('language') || 
      taxSlug.includes('translation') ||
      taxSlug === 'lang' ||
      tax.name?.toLowerCase().includes('language')) {
    results.languageTaxonomies.push({
      slug: taxSlug,
      details: tax
    });
  }
}

// Generate recommendations
if (results.languageTaxonomies.length > 0) {
  results.recommendations.push('Language taxonomy found: ' + results.languageTaxonomies[0].slug);
  results.recommendations.push('Use this taxonomy when creating posts by adding it to the taxonomies parameter');
} else {
  results.recommendations.push('No dedicated language taxonomy found');
  results.recommendations.push('Language might be handled through post meta or custom fields');
}

return results;

// Check API routes for Polylang endpoints
const results = {
  polylangRoutes: [],
  allNamespaces: [],
  recommendations: []
};

const items = $input.all();
if (items.length === 0 || !items[0].json) {
  return { error: 'No route data received' };
}

const apiData = items[0].json;

// Check namespaces
if (apiData.namespaces) {
  results.allNamespaces = apiData.namespaces;

  // Look for Polylang namespace
  const pllNamespaces = apiData.namespaces.filter(ns => 
    ns.includes('pll') || 
    ns.includes('polylang') || 
    ns.includes('language')
  );

  if (pllNamespaces.length > 0) {
    results.polylangRoutes = pllNamespaces;
    results.recommendations.push('Polylang API namespace found: ' + pllNamespaces.join(', '));
    results.recommendations.push('Explore these endpoints for language management');
  }
}

// Check routes
if (apiData.routes) {
  for (const route in apiData.routes) {
    if (route.includes('pll') || 
        route.includes('language') || 
        route.includes('translation')) {
      results.polylangRoutes.push({
        route: route,
        methods: apiData.routes[route].methods
      });
    }
  }
}

if (results.polylangRoutes.length === 0) {
  results.recommendations.push('No dedicated Polylang routes found');
  results.recommendations.push('Language handling is likely integrated into standard WP endpoints');
}

return results;
