// Group walls by Type Name and sum Volume
// Using only built-in JavaScript functions

// Get all wall elements
const allWalls = $input.all().map(item => item.json);

// Group by type_name using reduce
const grouped = allWalls.reduce((acc, wall) => {
  const typeName = wall.type_name;
  if (!acc[typeName]) {
    acc[typeName] = [];
  }
  acc[typeName].push(wall);
  return acc;
}, {});

// Create grouping results
const groupedResults = Object.keys(grouped).map(typeName => {
  const walls = grouped[typeName];
  const totalVolume = walls.reduce((sum, wall) => sum + (wall.volume || 0), 0);
  const count = walls.length;

  return {
    type_name: typeName,
    wall_count: count,
    total_volume: Math.round(totalVolume * 1000) / 1000, // round to 3 decimal places
    average_volume: count > 0 ? Math.round((totalVolume / count) * 1000) / 1000 : 0,
    processed_date: new Date().toISOString()
  };
});

// Sort by total volume descending
const sortedResults = groupedResults.sort((a, b) => b.total_volume - a.total_volume);

return sortedResults.map(item => ({ json: item }));

// Generate HTML report from grouped wall data
