// Simplified URL extraction from Perplexity response
const perplexityData = items[0].json;
let content = '';

// Handle different response structures
if (perplexityData.message && perplexityData.message.content) {
  content = perplexityData.message.content;
} else if (perplexityData.content) {
  content = perplexityData.content;
} else {
  content = JSON.stringify(perplexityData);
}

// Extract URL using multiple patterns
let articleURL = '';

// Pattern 1: Look for "Article URL:" format
const urlPattern1 = content.match(/Article URL:\s*(https?:\/\/[^\s]+)/i);
if (urlPattern1 && urlPattern1[1]) {
  articleURL = urlPattern1[1];
} else {
  // Pattern 2: Look for any URL in the content
  const urlPattern2 = content.match(/(https?:\/\/[^\s]+)/i);
  if (urlPattern2 && urlPattern2[1]) {
    articleURL = urlPattern2[1];
  } else {
    // Default fallback URL
    articleURL = 'https://example.com';
  }
}

// Clean the URL - remove trailing punctuation and whitespace
articleURL = articleURL.replace(/["'\]\)\}>\[,;.!?]+$/, '').trim();

// Parse script data if it exists
let scriptData = {};
try {
  const scriptNode = $('Write Video Script');
  if (scriptNode && scriptNode.item && scriptNode.item.json) {
    let scriptContent = '';

    // Get content without optional chaining
    if (scriptNode.item.json.content) {
      scriptContent = scriptNode.item.json.content;
    } else if (scriptNode.item.json.message && scriptNode.item.json.message.content) {
      scriptContent = scriptNode.item.json.message.content;
    }

    // Try to parse as JSON first
    try {
      scriptData = JSON.parse(scriptContent);
      // Clean the sourceURL in scriptData too
      if (scriptData.sourceURL) {
        scriptData.sourceURL = scriptData.sourceURL.replace(/["'\]\)\}>\[,;.!?]+$/, '').trim();
      }
    } catch (e) {
      // If not JSON, use the content as script
      scriptData = {
        script: scriptContent,
        sourceURL: articleURL
      };
    }
  }
} catch (e) {
  console.error('Error parsing script data:', e);
}

return [{
  json: {
    sourceURL: articleURL,
    encodedURL: encodeURIComponent(articleURL),
    scriptData: scriptData,
    perplexityContent: content
  }
}];

// Get data from all required nodes
  const captionNode = $items('Write Video Caption')[0];
  const extractUrlNode = $items('Extract URL')[0];
  const tmpfilesNode = $items('Extract tmpfiles')[0];

  // Get the direct video URL from Cloudinary
  const backgroundVideoUrl = tmpfilesNode?.json?.directUrl || '';

  // Log for debugging
  console.log('=== PREPARE AVATAR DATA ===');
  console.log('Background Video URL:', backgroundVideoUrl);
  console.log('Script:', extractUrlNode?.json?.scriptData?.script);
  console.log('Script Length:', extractUrlNode?.json?.scriptData?.script?.length);

  // Validate we have all required data
  if (!backgroundVideoUrl) {
    throw new Error('Background video URL is missing');
  }

  if (!extractUrlNode?.json?.scriptData?.script) {
    throw new Error('Script text is missing');
  }

  // Ensure the video URL is publicly accessible
  if (!backgroundVideoUrl.includes('cloudinary.com')) {
    console.warn('Video URL might not be publicly accessible:', backgroundVideoUrl);
  }

  return [{
    json: {
      caption: captionNode?.json?.content || captionNode?.json?.message?.content || 'Video Caption',
      scriptData: extractUrlNode?.json?.scriptData || {},
      encodedURL: extractUrlNode?.json?.encodedURL || '',
      sourceURL: extractUrlNode?.json?.sourceURL || '',
      backgroundVideoUrl: backgroundVideoUrl,
      // Add video duration estimate based on script length
      estimatedDuration: Math.max(10, (extractUrlNode?.json?.scriptData?.script?.length || 0) / 10)
    }
  }];

// Prepare data for Google Sheets
const videoCreationResponse = $input.first().json;
const captionNode = $items('Write Video Caption')[0];
const caption = captionNode?.json?.content || captionNode?.json?.message?.content || 'Video Caption';
const videoId = videoCreationResponse.data?.video_id || '';

console.log("=== PREPARING SHEETS DATA ====");
console.log("Video ID:", videoId);
console.log("Caption:", caption);
console.log("=============================");

// Format data for Google Sheets
return [{
  json: {
    video_id: videoId,
    caption: caption,
    timestamp: new Date().toISOString(),
    status: 'processing'
  }
}];

// Extract data from HeyGen webhook callback
  const webhookData = $input.first().json.body;
  const eventType = webhookData.event_type;
  const eventData = webhookData.event_data;

  console.log("=== HEYGEN WEBHOOK RECEIVED ===");
  console.log("Event Type:", eventType);
  console.log("Video ID:", eventData?.video_id);
  console.log("Video URL:", eventData?.url);
  console.log("===============================");

  // Check if video succeeded
  if (eventType === "avatar_video.success") {
    return [{
      json: {
        video_id: eventData.video_id,
        video_url: eventData.url,
        gif_url: eventData.gif_download_url,
        share_url: eventData.video_share_page_url,
        callback_id: eventData.callback_id,
        status: 'completed',
        ready_for_upload: true
      }
    }];
  } else if (eventType === "avatar_video.fail") {
    throw new Error(`Video generation failed: ${eventData.msg}`);
  } else {
    throw new Error(`Unknown event type: ${eventType}`);
  }

// Combine video data with caption from sheets
const videoData = $items('Process HeyGen Callback')[0].json;
const sheetData = $input.first().json;

console.log("=== COMBINING DATA ===");
console.log("Video URL:", videoData.video_url);
console.log("Caption from Sheets:", sheetData.caption);
console.log("====================");

return [{
  json: {
    video_url: videoData.video_url,
    video_id: videoData.video_id,
    caption: sheetData.caption || 'Video Caption',
    status: 'completed',
    ready_for_upload: true
  }
}];

