parameters.inputSchema:
{
    "type": "object",
    "properties": {
        "ready": {
            "type": "boolean",
            "description": "After generating the blog post content, this boolean indicates if it's ready for publishing."
        },
        "table_of_content": {
            "type": "string",
            "description": "the table of the content ToC"
        },
        "final_blog_post_html": {
            "type": "string",
            "description": "The generated blog post HTML content."
        },
        "meta_data": {
            "type": "object",
            "properties": {
                "title": {
                    "type": "string"
                },
                "slug": {
                    "type": "string"
                },
                "description": {
                    "type": "string"
                }
            }
        },
        "image_data": {
            "type": "object",
            "description": "Image data contains metadata and prompt for generation.",
            "properties": {
                "alt_text": {
                    "type": "string",
                    "description": "Alt text of the image."
                },
                "image_prompt": {
                    "type": "string",
                    "description": "Prompt used to generate the image."
                }
            }
        }
    },
    "required": [
        "ready",
        "final_blog_post_html"
    ]
}

{
    "type": "object",
    "properties": {
        "image_data": {
            "type": "object",
            "description": "image data contain image_prompt, alt_text",
            "properties": {
                "alt_text": {
                    "type": "string",
                    "description": "this alt text of the image"
                },
                "image_prompt": {
                    "type": "string",
                    "description": "this is the prompt of the image, which will be used to generate an image"
                }
            }
        }
    }
}
