const inputData = $input.all();
const commits = inputData[0].json.body.commits;

return commits.map(commit => {
  const message = commit.message;
  const timestamp = commit.timestamp;

  // Match something like "ABC-123", case-insensitive, from the beginning
  const match = message.match(/([A-Z]+-\d+)/i);

  const jiraId = match ? match[0].toUpperCase() : null;

  return {
    json: {
      message,
      timestamp,
      jira_id: jiraId
    }
  };
});

// Get all incoming items from the JIRA node.
const allItems = $input.all();

// Use .map() to loop through each item and transform it.
return allItems.map(item => {
  // The full JIRA issue data for the current item in the loop
  const issue = item.json;

  // Extract the specific fields you need for this issue.
  // Use optional chaining (?.) to prevent errors if a field is missing.
  const jiraId = issue.key; // The JIRA ID (e.g., "MS-6")
  const summary = issue.fields?.summary;
  const description = issue.fields?.description;

  // Return a new, clean object for this specific issue.
  return {
    json: {
      jira_id: jiraId, // Added as requested
      jira_summary: summary,
      jira_description: description
    }
  };
});

// This will take all input items from previous loop and combine into one array
return [{
  json: {
    items: items.map(i => i.json)
  }
}];
