// Get all trends from the spreadsheet
const trends = $('Read Trends Data').all().map(item => item.json.Trend).filter(trend => trend);

// Random selection
const randomTopic = trends[Math.floor(Math.random() * trends.length)];

// For cyclic selection, you could implement a counter mechanism
// This is a simplified version using random selection

return {
  json: {
    Selected_Topic: randomTopic,
    Available_Trends: trends,
    Selection_Method: 'Random'
  }
};

// Get the raw AI output (string with possible markdown)
let rawOutput = $node["Prepare Newsletter Data"].json.output;

// Strip markdown code block if present (```json ... ```)
rawOutput = rawOutput.replace(/```json|```/g, '').trim();

// Parse cleaned JSON string
const aiContent = JSON.parse(rawOutput);

// Get template and selected topic
const templateContent = $node["Extract from Text File"].json.data;
const selectedTopic = $json.Selected_Topic || 'Thema fehlt';

// Helper for safe replacements
const getSafe = (obj, key) => obj?.[key] ?? 'undefined';

// Replace placeholders for German content
let newsletterContent = templateContent;
newsletterContent = newsletterContent.replace(/\{\{INTRODUCTION_DE\}\}/g, getSafe(aiContent, 'INTRODUCTION_DE'));
newsletterContent = newsletterContent.replace(/\{\{TOPIC_DE\}\}/g, getSafe(aiContent, 'TOPIC_DE'));
newsletterContent = newsletterContent.replace(/\{\{DESCRIPTION_DE\}\}/g, getSafe(aiContent, 'DESCRIPTION_DE'));
newsletterContent = newsletterContent.replace(/\{\{TEASER1_DE\}\}/g, getSafe(aiContent, 'TEASER1_DE'));
newsletterContent = newsletterContent.replace(/\{\{TEASER2_DE\}\}/g, getSafe(aiContent, 'TEASER2_DE'));
newsletterContent = newsletterContent.replace(/\{\{TEASER3_DE\}\}/g, getSafe(aiContent, 'TEASER3_DE'));
newsletterContent = newsletterContent.replace(/\{\{CTA_DE\}\}/g, getSafe(aiContent, 'CTA_DE'));
newsletterContent = newsletterContent.replace(/\{\{FOOTER_TEXT_DE\}\}/g, getSafe(aiContent, 'FOOTER_TEXT_DE'));
newsletterContent = newsletterContent.replace(/\{\{FOOTER_NAME_DE\}\}/g, getSafe(aiContent, 'FOOTER_NAME_DE'));

// Replace placeholders for English content
newsletterContent = newsletterContent.replace(/\{\{TOPIC_TITLE_EN\}\}/g, getSafe(aiContent, 'TOPIC_TITLE_EN'));
newsletterContent = newsletterContent.replace(/\{\{INTRODUCTION_EN\}\}/g, getSafe(aiContent, 'INTRODUCTION_EN'));
newsletterContent = newsletterContent.replace(/\{\{TOPIC_EN\}\}/g, getSafe(aiContent, 'TOPIC_EN'));
newsletterContent = newsletterContent.replace(/\{\{DESCRIPTION_EN\}\}/g, getSafe(aiContent, 'DESCRIPTION_EN'));
newsletterContent = newsletterContent.replace(/\{\{TEASER1_EN\}\}/g, getSafe(aiContent, 'TEASER1_EN'));
newsletterContent = newsletterContent.replace(/\{\{TEASER2_EN\}\}/g, getSafe(aiContent, 'TEASER2_EN'));
newsletterContent = newsletterContent.replace(/\{\{TEASER3_EN\}\}/g, getSafe(aiContent, 'TEASER3_EN'));
newsletterContent = newsletterContent.replace(/\{\{CTA_EN\}\}/g, getSafe(aiContent, 'CTA_EN'));
newsletterContent = newsletterContent.replace(/\{\{FOOTER_TEXT_EN\}\}/g, getSafe(aiContent, 'FOOTER_TEXT_EN'));
newsletterContent = newsletterContent.replace(/\{\{FOOTER_NAME_EN\}\}/g, getSafe(aiContent, 'FOOTER_NAME_EN'));

// Add date if needed (you can add this to template if required)
newsletterContent = newsletterContent.replace(/\{\{DATE\}\}/g, new Date().toLocaleDateString('de-DE'));

// Return final output
return {
  json: {
    newsletter_content: newsletterContent,
    topic: selectedTopic,
    topic_de: getSafe(aiContent, 'TOPIC_DE'),
    topic_en: getSafe(aiContent, 'TOPIC_EN'),
    generated_date: new Date().toISOString(),
    // Include all generated content for debugging/reference
    ai_content: aiContent
  }
};

