const extractData = $('Extract').first().json;

// Ekstrak icon URL dengan error handling
let iconUrl = null;

try {
  // Periksa apakah external dan icon ada
  if (extractData?.external?.icon) {
    const iconObject = extractData.external.icon;

    // Ambil key pertama (URL) dari object icon
    const iconKeys = Object.keys(iconObject);

    if (iconKeys.length > 0) {
      iconUrl = iconKeys[0];
    }
  }
} catch (error) {
  console.error('Error extracting icon URL:', error);
}

// Return hasil
return [
  {
    json: {
      iconUrl: iconUrl || '', // Return empty string jika tidak ada icon
      // Atau bisa juga return original data + iconUrl
      ...extractData,
      processedIconUrl: iconUrl
    }
  }
];

