const input = $input.item.json;
const subdomain = input.subdomain || "unknown.subdomain";

// Explicitly access each DNS record type
const dnsRecords = {
  A: input.a || [],
  AAAA: input.aaaa || [],
  CNAME: input.cname || [],
  TXT: input.txt || [],
  MX: input.mx || [],
  NS: input.ns || [],
  SOA: input.soa || []
};

let markdown = `### DNS Records for: ${subdomain}\n\n`;

for (const [type, records] of Object.entries(dnsRecords)) {
  if (Array.isArray(records) && records.length > 0) {
    const formatted = records.map(r => `- ${r}`).join('\n');
    markdown += `**${type}**:\n${formatted}\n\n`;
  } else {
    markdown += `**${type}**:\n- No records found.\n\n`;
  }
}

return {
  json: {
    subdomain,
    dns_markdown: markdown.trim()
  }
};

// Get raw text from DNS Lookup node
const dnsText = $json.dns || "No data";

// Prepare output object
const recordTypes = ['A', 'AAAA', 'CNAME', 'TXT', 'MX', 'NS', 'SOA'];
const result = {
  subdomain: $json.subdomain || "unknown.subdomain"
};

// Initialize arrays for each type
for (const type of recordTypes) {
  result[type.toLowerCase()] = [];
}

// Parse each line
dnsText.split('\n').forEach(line => {
  const match = line.match(/^(\w+)\s*:\s*(.*)$/);
  if (match) {
    const type = match[1].toUpperCase();
    const value = match[2].trim();
    if (recordTypes.includes(type)) {
      result[type.toLowerCase()].push(value);
    }
  }
});

// If no values found, insert "No records found"
for (const type of recordTypes) {
  if (result[type.toLowerCase()].length === 0) {
    result[type.toLowerCase()].push("No records found.");
  }
}

return [ { json: result } ];

