const cve = items[0].json;
const iocs = items[1].json.iocs || [];

return [
  {
    json: {
      cve,
      iocs
    }
  }
];

const data = $input.all();
return data.map((item, i) => {
  const baseScore = item.json.cve?.impact?.baseMetricV3?.cvssV3?.baseScore || 0;
  const aiRisk = [6.5, 9.1][i] || 5;
  const path = ["self-healing", "expert-review", "monitoring"][i % 3];
  const lev = [0.93, 0.72][i] || 0.45;

  return {
    json: {
      ...item.json,
      aiRisk,
      path,
      lev
    }
  };
});

const triage = {
  self: [],
  expert: [],
  monitor: [],
};

const assessed = $input.all();

for (const item of assessed) {
  const v = item.json;
  const levScore = v.lev || 0; // fallback if missing

  if (levScore > 0.9) {
    triage.expert.push({ ...v, levScore, levLabel: "Critical" });
  } else if (levScore > 0.5) {
    triage.self.push({ ...v, levScore, levLabel: "High" });
  } else {
    triage.monitor.push({ ...v, levScore, levLabel: "Low" });
  }
}

return [{ json: triage }];

const threat = $json;
const score = threat.Score || 0;
const severity = (threat.Severity || "").toUpperCase();

let playbook = "notify"; // Default response

if (score >= 9 || severity === "CRITICAL") {
  playbook = "isolation";
} else if (score >= 6 || severity === "HIGH") {
  playbook = "monitor";
}

return [
  {
    json: {
      ...threat,
      response: {
        playbook: playbook,
        decisionReason: `Based on CVSS ${score} and severity ${severity}`
      }
    }
  }
];

