parameters.jsCode:
const fromToken = $input.first().json.body.fromToken;
const fromChain = $input.first().json.body.fromChain;

const config = {
  "0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913": {
    tokenAddress: "0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913",
    chain: "8453",
    name: "USD Coin",
    version: "2",
    contractMethodId: "c181f867-b03c-4a34-a8b1-de3cc05021c1"
  },
  "0xB97EF9Ef8734C71904D8002F8b6Bc66Dd9c48a6E": {
    tokenAddress: "0xB97EF9Ef8734C71904D8002F8b6Bc66Dd9c48a6E",
    chain: "43114",
    name: "USD Coin",
    version: "2",
    contractMethodId: "d359bee6-83f5-4e0d-972c-5385c0f6ece2"
  },
  "0xaf88d065e77c8cc2239327c5edb3a432268e5831": {
    tokenAddress: "0xaf88d065e77c8cc2239327c5edb3a432268e5831",
    chain: "42161",
    name: "USD Coin",
    version: "2",
    contractMethodId: "507a44d3-42c9-45e8-9101-ab6cfa42be68"
  }
};

// Check if fromToken exists in config
if (!config[fromToken]) {
  throw new Error(`Token ${fromToken} not found in configuration`);
}

// Optionally you could also validate the chain if needed
if (config[fromToken].chain !== fromChain) {
  throw new Error(`Invalid chain for token ${fromToken}`);
}

// Add the parsed JSON object to the input
$input.first().json.paymentConfig = config[fromToken];

return $input.all();

