try {
    // Decode the x-payment header from base64
    const xPaymentHeader = $input.first().json.body.paymentHeader;
    const decodedXPayment = Buffer.from(xPaymentHeader, 'base64').toString('utf-8');

    // Parse the decoded value into a JSON object
    const decodedXPaymentJson = JSON.parse(decodedXPayment);

    // Add the parsed JSON object to the input
    $input.first().json.decodedXPayment = decodedXPaymentJson;

    return $input.all();
} catch (error) {
    // Return an error object if the token format is invalid
    return { error: "invalid token format" };
}

