// Ce script prend la sortie texte brute de l'Agent IA,
// la nettoie, et la transforme en un objet JSON valide.

// La sortie de l'Agent IA (en mode String) est dans la propriété 'output'.
const rawText = $input.item.json.output;

// Vérification de robustesse : s'assurer que rawText existe avant de le traiter.
if (typeof rawText !== 'string' || rawText.trim() === '') {
  // Si rawText n'est pas une chaîne de caractères ou est vide, l'Agent IA a probablement échoué.
  console.error("L'entrée de l'Agent IA est manquante ou n'est pas une chaîne de caractères.", $input.item.json);
  throw new Error("La sortie de l'Agent IA est vide ou invalide. Vérifiez l'exécution du nœud 'AI Agent'.");
}

// Étape 1: Nettoyer le texte. On enlève les blocs de code markdown (```json ... ```) que l'IA ajoute souvent.
let cleanedText = rawText.replace(/^```json\s*/, '').replace(/```$/, '');

// Étape 2: Trouver le début et la fin de l'objet JSON pour enlever tout texte superflu.
const firstBracket = cleanedText.indexOf('{');
const lastBracket = cleanedText.lastIndexOf('}');

if (firstBracket === -1 || lastBracket === -1) {
  throw new Error("La réponse de l'IA ne contient pas de JSON valide (accolades manquantes).");
}
cleanedText = cleanedText.substring(firstBracket, lastBracket + 1);

try {
  // Étape 3: Tenter de parser le texte nettoyé en objet JSON.
  const jsonData = JSON.parse(cleanedText);
  // Retourner l'objet JSON propre pour le nœud suivant.
  return { json: jsonData };
} catch (error) {
  // Si le parsing échoue, enregistrer les détails pour le débogage.
  console.error("Erreur de parsing JSON:", error);
  throw new Error(`Impossible de parser la réponse de l'IA. Réponse brute nettoyée: ${cleanedText}`);
}

// Ce script récupère tous les items (lignes) du noeud précédent.
const allItems = $input.all();

// On extrait la partie 'json' de chaque item pour ne garder que les données.
const jsonData = allItems.map(item => item.json);

// On crée un objet de sortie unique qui contient toutes les données
// converties en une chaîne de texte JSON.
const output = {
  data: JSON.stringify(jsonData)
};

// On retourne ce nouvel objet. Le workflow continuera avec un seul item.
return { json: output };

// Dans un nœud Code
const binaryData = $node["On form submission"].binary;
let filename = "";

if (binaryData && Object.keys(binaryData).length > 0) {
  const firstKey = Object.keys(binaryData)[0];
  const fileData = binaryData[firstKey];

  // Le nom peut être dans fileName ou filename
  filename = fileData.fileName || fileData.filename || "fichier_inconnu";
}

return [{ json: { filename } }];
