// v2 syntax â€“ must return an array of { json: {...} }
const lines = $json.affirmations
  .split('\n')
  .map(s => s.trim())
  .filter(Boolean);

const pick = lines.length
  ? lines[Math.floor(Math.random() * lines.length)]
  : 'I am enough.';

return [{
  json: {
    ...$json,
    affirmation: pick
  }
}];
