function generateShortUUID() {
  return Math.random().toString(36).substring(2, 14); // 12 chars
}

return [
  {
    json: {
      uuid: generateShortUUID()
    }
  }
];

// 1. Pega o conteúdo HTML
const htmlRaw = $json.data || $json.body;

if (!htmlRaw || typeof htmlRaw !== 'string') {
  throw new Error('Campo `data` ou `body` ausente ou inválido.');
}

// 2. Sanitiza o HTML
let cleaned = htmlRaw
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<!--[\s\S]*?-->/g, '')
  .replace(/<(head|header|footer|nav|button|form|aside|meta|link|iframe|noscript)[^>]*>[\s\S]*?<\/\1>/gi, '')
  .replace(/<[^>]+>/g, '') // remove tags HTML restantes
  .replace(/\s{2,}/g, ' ')
  .replace(/\n{2,}/g, '\n')
  .trim();

// 3. Pega os dados do nó anterior "process_url" (sem quebrar o fluxo)
const processData = $node["process_url"].json;

// 4. Retorna novo objeto com os metadados + texto sanitizado
return [
  {
    json: {
      uuid: processData.uuid,
      cloudProvider: processData.cloudProvider || processData.cloudprovider,
      technology: processData.technology,
      url: processData.url,
      sanitizedText: cleaned
    }
  }
];

const { uuid, cloudprovider, technology, urls } = $json;

return urls.map(url => ({
  json: {
    uuid,
    cloudProvider: cloudprovider,
    technology,
    url
  }
}));

// === ec_merge_data — append determinístico (.txt) ===

// 1) Lê o texto atual (prioriza binary.data.data do item corrente; fallback no nó de download)
function readPrevText() {
  // fonte 1: item atual
  let b64 = ($binary?.data && typeof $binary.data.data === 'string') ? $binary.data.data : '';

  // fonte 2: nó de download (caso o item atual esteja sem payload)
  if (!b64) {
    try {
      const dl = $items('ec_download_existing_file')[0];
      if (dl?.binary?.data?.data && typeof dl.binary.data.data === 'string') {
        b64 = dl.binary.data.data;
      }
    } catch {}
  }

  // fonte 3: raros casos de texto no JSON
  if (!b64) {
    if (typeof $json?.data === 'string')    return $json.data.replace(/\r\n/g, '\n');
    if (typeof $json?.body === 'string')    return $json.body.replace(/\r\n/g, '\n');
    if (typeof $json?.content === 'string') return $json.content.replace(/\r\n/g, '\n');
    return '';
  }

  try { return Buffer.from(b64, 'base64').toString('utf8').replace(/\r\n/g, '\n'); }
  catch { return ''; }
}

const prevText = readPrevText();

// 2) Texto novo direto do "1_DefySec_Extractor"
let ext;
try { ext = $items('1_DefySec_Extractor')[0]?.json; } catch {}
if (!ext) { try { ext = $node['1_DefySec_Extractor']?.json; } catch {} }

let newText = ext?.output ?? ext?.data ?? ext?.text ?? '';
newText = String(newText)
  .replace(/^```(?:txt|text|json)?\s*/i, '')   // remove cercas, se vierem
  .replace(/\s*```$/, '')
  .replace(/\r\n/g, '\n');

// 3) Append (1 linha em branco entre blocos quando já existe conteúdo)
const combined = prevText
  ? prevText.replace(/\s*$/, '') + '\n\n' + newText.replace(/^\s+/, '')
  : newText;

// 4) Empacota binário para o Update (sempre em binary.data)
const outB64 = Buffer.from(combined, 'utf8').toString('base64');

// 5) Garante fileId/fileName para o Update
let { fileId, fileName } = $json;
if (!fileId || !fileName) {
  try {
    const src = $items('extracted_controls_append_or_create')[0]?.json;
    fileId   = fileId   || src?.fileId;
    fileName = fileName || src?.fileName;
  } catch {}
}

// 6) **RETORNA UM ARRAY** com 1 item (é isso que o n8n exige)
return [{
  json: {
    fileId,
    fileName,
    prevBytes: prevText.length,
    newBytes: newText.length,
    mergedBytes: combined.length
  },
  binary: {
    data: {
      data: outB64,
      mimeType: 'text/plain',
      fileName
    }
  }
}];

// === Code: decide append/create e prepara dados ===

// util
function safe(node) {
  try { const a = $items(node); if (a?.[0]?.json) return a[0].json; } catch {}
  try { const j = $node[node]?.json; if (j) return j; } catch {}
  return {};
}

// 1) Contexto
const settings = safe('settings');
const uuid = String(settings.uuid || '').trim();
const folderId = String(settings.gdrive_target || '').trim();
if (!uuid) throw new Error('uuid ausente no nó "settings".');
if (!folderId) throw new Error('gdrive_target (folderId) ausente no nó "settings".');

const canonicalName = `${uuid}_extractedControls.txt`;

// 2) Normaliza retorno do List para array de arquivos
const files = [];
for (const it of items) {
  const j = it?.json;
  if (Array.isArray(j)) files.push(...j);
  else if (j && (j.name || j.id)) files.push(j);
}
const existing = files.find(f => f.name === canonicalName);

// 3) Pega TEXTO do "1_DefySec_Extractor"
const ext = safe('1_DefySec_Extractor');
let newText = ext.output ?? ext.data ?? ext.text ?? $json.output ?? '';
newText = String(newText)
  .replace(/^```(?:txt|text|json)?\s*/i, '')
  .replace(/\s*```$/, '')
  .replace(/\r\n/g, '\n');

// 4) Decide ação
const out = {
  action: existing ? 'append' : 'create',
  fileId: existing?.id || null,
  fileName: canonicalName,
  folderId,
  newText,
};

// 5) Se for criação, já emite binário pra Upload; se for append, só metadados.
if (!existing) {
  const base64 = Buffer.from(newText, 'utf8').toString('base64');
  return [{
    json: out,
    binary: {
      data: {
        data: base64,
        mimeType: 'text/plain',
        fileName: canonicalName
      }
    }
  }];
}

return [{ json: out }];

// === ec_extract_content — lê do Google Drive (ou do item atual) e repassa ===
// Saída: [{ json: { content, fileId, fileName, mimeType, length } }]

function readFromCurrentItem() {
  const b = $binary?.data;
  if (b?.data && typeof b.data === 'string') {
    return {
      b64: b.data,
      fileName: b.fileName,
      mimeType: b.mimeType,
    };
  }
  return null;
}

function readFromDownloadNode() {
  try {
    const dl = $items('ec_download_existing_file')[0];
    const b = dl?.binary?.data;
    if (b?.data && typeof b.data === 'string') {
      // fileId/fileName podem também estar em dl.json dependendo do seu fluxo
      return {
        b64: b.data,
        fileName: b.fileName || dl?.json?.fileName,
        mimeType: b.mimeType || dl?.json?.mimeType,
        fileId: dl?.json?.fileId,
      };
    }
  } catch {}
  return null;
}

function readTextFallback() {
  // Casos raros em que veio texto no JSON
  if (typeof $json?.data === 'string')    return $json.data;
  if (typeof $json?.body === 'string')    return $json.body;
  if (typeof $json?.content === 'string') return $json.content;
  return '';
}

function b64ToUtf8(b64) {
  try {
    return Buffer.from(b64, 'base64').toString('utf8');
  } catch {
    return '';
  }
}

function normalize(text) {
  return String(text)
    .replace(/^\uFEFF/, '')           // remove BOM, se houver
    .replace(/^```(?:txt|text|json)?\s*/i, '') // remove cercas de código no início
    .replace(/\s*```$/, '')           // remove cerca de fechamento
    .replace(/\r\n/g, '\n');          // normaliza quebras de linha
}

// --- Coleta do conteúdo ---
let meta = readFromCurrentItem();
if (!meta) meta = readFromDownloadNode();

let text = '';
let fileName, mimeType, fileId;

if (meta?.b64) {
  text = b64ToUtf8(meta.b64);
  fileName = meta.fileName;
  mimeType = meta.mimeType;
  fileId   = meta.fileId;
} else {
  text = readTextFallback();
}

// Normaliza e prepara saída
text = normalize(text);

// Tenta herdar fileId/fileName do JSON atual se não vieram do download
if (!fileId)   fileId   = $json?.fileId;
if (!fileName) fileName = $json?.fileName;
if (!mimeType) mimeType = $json?.mimeType || 'text/plain';

return [{
  json: {
    content: text,
    fileId,
    fileName,
    mimeType,
    length: text.length,
  }
}];

