// === make_file_from_output ===
// Lê o(s) "output(s)" recebido(s) e gera 1 arquivo texto em binary.data
// Saída: [{ json: { fileName, mimeType, bytes }, binary: { data: { data, mimeType, fileName } } }]

function extractOutputFromJson(j) {
  if (j == null) return '';
  if (typeof j === 'string') return j;

  if (Array.isArray(j)) {
    // junta todos os outputs de um array (se houver mais de um)
    const parts = j
      .map(o => (o && typeof o.output === 'string') ? o.output : '')
      .filter(Boolean);
    if (parts.length) return parts.join('\n\n---\n\n');
    // fallback: se o array não tem "output", tenta serializar
    try { return JSON.stringify(j, null, 2); } catch { return ''; }
  }

  if (typeof j === 'object') {
    if (typeof j.output === 'string')   return j.output;
    if (typeof j.content === 'string')  return j.content;
    if (typeof j.data === 'string')     return j.data;
    if (typeof j.body === 'string')     return j.body;
    try { return JSON.stringify(j, null, 2); } catch {}
  }
  return '';
}

function stripCodeFences(s) {
  return String(s)
    .replace(/^\s*```[a-z]*\s*/i, '') // remove ```txt / ```json / ``` etc no início
    .replace(/\s*```[\s\r\n]*$/i, '') // remove ``` no fim
    .replace(/^\uFEFF/, '')           // remove BOM se houver
    .replace(/\r\n/g, '\n');          // normaliza quebras
}

function detectTechnology(s) {
  const m = s.match(/Technology:\s*([^\n]+)/i);
  return m ? m[1].trim().replace(/[^\w.-]+/g, '_') : null;
}

const itemsIn = $input.all();

// Coleta e normaliza conteúdos
const contents = [];
for (const it of itemsIn) {
  const raw = extractOutputFromJson(it.json);
  if (!raw) continue;
  contents.push(stripCodeFences(raw));
}

const content = (contents.join('\n\n---\n\n') || '').trim();

// Gera nome de arquivo
const tech = detectTechnology(content) || 'output';
const ts = new Date().toISOString().replace(/[:.]/g, '-'); // seguro para filename
const fileName = `controls_${tech}_${ts}.txt`;
const mimeType = 'text/plain';

// Se não houver conteúdo, ainda retornamos um arquivo com nota (evita quebrar o fluxo)
const finalText = content || 'NO_CONTENT_EXTRACTED';
const bytes = Buffer.byteLength(finalText, 'utf8');
const b64 = Buffer.from(finalText, 'utf8').toString('base64');

// Retorno no formato esperado pelo n8n
return [{
  json: { fileName, mimeType, bytes },
  binary: {
    data: {
      data: b64,
      mimeType,
      fileName,
    },
  },
}];

// Lê todos os itens de entrada do nó anterior
const all = $input.all(); // [{json: {...}}, ...]

// Normaliza: pode ser (a) vários itens simples, (b) 1 item com `data[]`, (c) 1 item com array plano
let list;
if (all.length === 1 && Array.isArray(all[0].json?.data)) {
  list = all[0].json.data;                // caso: { data: [...] }
} else if (all.length === 1 && Array.isArray(all[0].json)) {
  list = all[0].json;                     // caso: [{id,name,model}, ...] dentro do json
} else {
  list = all.map(i => i.json);            // caso: cada item já é {id,name,model}
}

// Helpers: escolha por nome (ajuste os regex se renomear seus assistants)
const pick = (re) => list.find(a => re.test(String(a.name || '')));

// Heurística pelos seus nomes:
const extractor = pick(/(^|[\s_-])1[\s_-]*DefySec[\s_-]*Extractor\b/i) || pick(/\bExtractor\b/i);
const composer  = pick(/(^|[\s_-])2[\s_-]*DefySec[\s_-]*Control[\s_-]*Composer\b/i) || pick(/\bComposer\b/i);
const baseline  = pick(/(^|[\s_-])3[\s_-]*DefySec[\s_-]*Baseline\b/i) || pick(/\bBaseline\b/i);

// Saída única com os IDs resolvidos
return [
  {
    json: {
      assistant_extractor_id: extractor?.id || '',
      assistant_composer_id:  composer?.id  || '',
      assistant_baseline_id:  baseline?.id  || ''
    }
  }
];
