// Get the current date and time
const now = new Date();

// Combine the output from all RSS feed nodes into one array
const allArticles = items.flatMap(item => item.json);

// Filter out articles older than 24 hours
const filteredArticles = allArticles.filter(item => {
    const pubDate = new Date(item.pubDate);
    const diff = (now - pubDate) / (1000 * 60 * 60);
    return diff <= 24;
});

// Extract key fields including publish date
const articlesText = filteredArticles.map(item => {
    return `\nTitle: ${item.title}\nLink: ${item.link}\nPublished: ${item.pubDate}\nText snippet: ${item.contentSnippet}\n---\n`;
}).join('');

// Return the filtered and formatted articles
return [{ json: { articlesText } }];

function convertToHTML(data) {
  const categoryMap = {
    activity_nation_state_actors: "Nation State Actors",
    activity_financially_motivated_actors: "Financially Motivated Actors",
    activity_compromises_and_data_breaches: "Compromises and Data Breaches",
    activity_vulnerabilities: "Vulnerabilities",
    activity_miscellaneous: "Miscellaneous"
  };

  const input = data[0]?.json?.output;
  if (!input) return [{ htmlOutput: '' }];

  let html = '';

  Object.keys(categoryMap).forEach((key) => {
    const entries = input[key];
    if (!Array.isArray(entries) || entries.length === 0) return;

    html += `<h4>${categoryMap[key]}</h4>`;

    entries.forEach((item) => {
      html += `- ${item.news_item}`;

      if (Array.isArray(item.references) && item.references.length > 0) {
        const sources = item.references.map((ref, idx) => {
          const date = new Date(ref.publish_date);
          const formattedDate = `${String(date.getDate()).padStart(2, '0')}/${String(date.getMonth() + 1).padStart(2, '0')}/${String(date.getFullYear()).slice(-2)}`;
          return `<a href="${ref.link}" target="_blank" rel="noopener">${ref.website_name} (${formattedDate})</a>`;
        }).join(', ');
        html += ` (${sources})`;
      }

      html += '<br />';
    });
  });

  return [{ htmlOutput: html }];
}

return convertToHTML($input.all());

const merged = items.map(e => e.json);
return [{
  json: {
    merged_reports: merged
  }
}];

const reports = items[0].json.output;

const filtered = reports.filter(report => report.references.length >= 3);

const html = filtered.map(report => {
  const references = report.references.map(ref => {
    const date = ref.publish_date ? new Date(ref.publish_date).toISOString().split('T')[0] : 'N/A';
    return `<li><a href="${ref.link}">${ref.website_name}</a> (${date})</li>`;
  }).join('');

  return `
    <section style="margin-bottom:2em;">
      <h2>${report.title}</h2>
      <p>${report.content}</p>
      <ul>${references}</ul>
    </section>
  `;
}).join('');

return [{
  json: {
    html: `<div>${html}</div>`
  }
}];
