// Code node: build prompts using fields from "When chat message received"
// Behavior:
// - If `output` exists => system: "Reword the blog..." ; user prompt uses `output` + `sessionId`
// - Else               => system: "Write a blog..."  ; user prompt uses `chatInput` + `sessionId`
// Both instruct to use the Google tool (sub-workflow) to count rows, passing ONLY the session id.
//
// Output fields:
//   - system_prompt
//   - user_prompt

// Pull from the trigger node if needed
const triggerItems = $items('Ask about blog topic', 0, 0) || [];
const trigger = triggerItems[0]?.json || {};

const schemaExample = JSON.stringify(
  { items: 5, session: "sessionid", blog: "blog" },
  null,
  0
);

return items.map(item => {
  const j = item.json || {};

  // Resolve chat and session from either the current item or the trigger node
  const chatInput = j.chatInput ?? j.chat ?? trigger.chatInput ?? "";
  const sessionId = j.sessionId ?? j.sessionid ?? trigger.sessionId ?? "";

  const hasOutput =
    j.output !== undefined &&
    j.output !== null &&
    String(j.output).trim() !== "";

  if (hasOutput) {
    // Reword existing blog
    item.json.system_prompt =
      "Reword the blog. Use the Google tool (sub-workflow) to count how many rows exist for this session. Pass ONLY the session id to the Google tool. it will send back the number.Include the session id from the input. Return ONLY valid JSON with the required keys.";
    item.json.user_prompt = [
      `sessionid: ${sessionId}`,
      `blog_to_reword: """${String(j.output)}"""`,
      `Respond as JSON exactly like: ${schemaExample}`
    ].join("\n");
  } else {
    // Write a new blog from chat
    item.json.system_prompt =
      "Write a blog. Use the Google tool (sub-workflow) to see how many rows exist for this session. Pass ONLY the session id to the Google tool. it will send back the number.  Include the session id from the input. Return ONLY valid JSON with the required keys.";
    item.json.user_prompt = [
      `sessionid: ${sessionId}`,
      `blog_topic_or_chat: """${String(chatInput)}"""`,
      `Respond as JSON exactly like: ${schemaExample}`
    ].join("\n");
  }

  // Optional debug
  item.json._resolved = { sessionId, chatInput, fromTrigger: !!triggerItems.length };

  return item;
});
