// IMPORTANT: Change this to the name of the node you want to get binary files from.
const previousNodeName = 'Get n8n Logo (Binary)';

// This will hold all the items we process.
const allItems = [];

// Loop over every item that is coming into this Code node.
for (const item of $input.all()) {

  // For the current item in the loop, find its corresponding item from the previous node.
  // The '.item' accessor automatically links the current item to its parent from the specified node.
  const previousNodeItem = $(previousNodeName).item;

  // Check if the corresponding item from the previous node actually has binary data.
  if (previousNodeItem && previousNodeItem.binary) {

    // Ensure the current item has a 'binary' object to add files to.
    // This also prevents overwriting any binary data the current item might already have.
    item.binary = item.binary || {};

    // Now, loop over ALL keys (each key represents a file) in the binary object.
    for (const inputPropertyName in previousNodeItem.binary) {

      // Get the specific binary file's data (which includes the base64 data and fileName).
      const binary = previousNodeItem.binary[inputPropertyName];

      // Decode the base64 data into a Buffer.
      const myBuffer = Buffer.from(binary.data, 'base64');

      // Use the n8n helper to prepare the binary data correctly for the next node
      // and attach it to the current item's binary object using its original property name.
      item.binary[inputPropertyName] = await this.helpers.prepareBinaryData(myBuffer, binary.fileName);
    }
  }

  // Add the processed item (with its new binary data) to our results array.
  allItems.push(item);
}

// Return all the processed items.
return allItems;
