/* ---------- 1) Markdown ‚Üí Facebook Markdown ---------- */
function mdToFacebook(md){
  let s = md;

  // code block ‚Üí gi·ªØ nguy√™n
  s = s.replace(/```([\s\S]*?)```/g, (m,p1)=>`\`\`\n${p1.trim()}\n\`\`\``);

  // inline code
  s = s.replace(/`([^`]+)`/g, (m,p1)=>`\`${p1}\``);

  // bold (**text**) ‚Üí *text*
  s = s.replace(/\*\*([^*]+)\*\*/g, '*$1*');

  // italic (_text_) ‚Üí gi·ªØ nguy√™n

  // underline & strike ‚Üí b·ªè
  s = s.replace(/__([^_]+)__/g, '$1');
  s = s.replace(/~~([^~]+)~~/g, '$1');

  // headers ‚Üí bold
  s = s.replace(/^(#{1,6})\s+(.+)$/gm, (m, hashes, content)=>`*${content.trim()}*`);

  // list markers ‚Üí bullet list Messenger
  s = s.replace(/^[\-\*\+]\s+(.+)$/gm, '* $1');

  // links ‚Üí gi·ªØ text
  s = s.replace(/\[([^\]]+)\]\(([^)]+)\)/g, (m,txt,url)=>txt);

  return s.trim();
}

/* ---------- 2) HTML ‚Üí Facebook Markdown ---------- */
function htmlToFbMarkdown(input){
  let s = input;

  s = s.replace(/<br\s*\/?>/gi, '\n');

  // headers ‚Üí bold
  s = s.replace(/<h[1-6][^>]*>([\s\S]*?)<\/h[1-6]>/gi, (m, inner)=>`*${inner.trim()}*`);

  // bold / italic
  s = s.replace(/<\/?strong[^>]*>/gi, '*');
  s = s.replace(/<\/?b[^>]*>/gi, '*');
  s = s.replace(/<\/?em[^>]*>/gi, '_');
  s = s.replace(/<\/?i[^>]*>/gi, '_');

  // paragraphs/divs ‚Üí newline
  s = s.replace(/<\/?(p|div)[^>]*>/gi, '\n');

  // lists ‚Üí bullet
  s = s.replace(/<li[^>]*>/gi, '* ').replace(/<\/li>/gi, '\n');
  s = s.replace(/<\/?(ul|ol)[^>]*>/gi, '');

  // remove all other tags
  s = s.replace(/<\/?[^>]+>/g, '');

  return s.trim();
}

/* ---------- 3) Normalize content ---------- */
function normalizeToFacebookMarkdown(input){
  const looksLikeMd = /(^|\s)[*_`~]|^#{1,6}\s|```/.test(input);
  let s = looksLikeMd ? mdToFacebook(input) : htmlToFbMarkdown(input);

  // collapse nhi·ªÅu newline th√†nh 1
  s = s.replace(/\n{3,}/g, '\n\n');

  // xo√° k√Ω t·ª± "\" th·ª´a c√≤n s√≥t
  s = s.replace(/\\+/g, '');

  return s.trim();
}

/* ---------- 4) Split by newlines (smart split) ---------- */
function splitMarkdownSmart(text, maxLen) {
  const lines = text.split('\n');
  const chunks = [];
  let buffer = '';

  for (const line of lines) {
    if ((buffer + line + '\n').length > maxLen) {
      if (buffer.trim()) chunks.push(buffer.trim());
      buffer = line + '\n';
    } else {
      buffer += line + '\n';
    }
  }

  if (buffer.trim()) chunks.push(buffer.trim());

  return chunks.map(c => c.replace(/\n+$/,''));
}

