const categories = [
  'Electronics',
  'Clothing & Fashion',
  'Home & Furniture',
  'Food & Beverages',
  'Sports & Outdoors',
  'Beauty & Personal Care'
];
const regions = [
  'North America',
  'South America',
  'Europe',
  'Asia',
  'Africa',
  'Oceania',
  'Middle East',
  'Central Asia'
];

function randomDateWithin730Days() {
  const now = new Date();
  const daysAgo = Math.floor(Math.random() * 730);
  now.setDate(now.getDate() - daysAgo);
  return now.toISOString().slice(0, 10);
}

let items = [];
for (let i = 0; i < 1000; i++) {
  items.push({
    json: {
      order_date: randomDateWithin730Days(),
      product_category: categories[Math.floor(Math.random() * categories.length)],
      region: regions[Math.floor(Math.random() * regions.length)],
      sales_amount: (50 + Math.random() * 49950).toFixed(2)
    }
  });
}
return items;
