const items = $input.all();

// Get base URL from previous node (make sure it's executed!)
const BASE_URL_RAW = $('Get Search URL').first().json.organization_website_url?.trim();
if (!BASE_URL_RAW) {
  throw new Error("Missing organization_website_url from 'Code1' node.");
}

const BASE_URL = BASE_URL_RAW.replace(/\/$/, ""); // remove trailing slash

const getHostname = (url) => {
  return url
    .replace(/^https?:\/\//, "")
    .replace(/^www\./, "")
    .split("/")[0];
};

const baseHostname = getHostname(BASE_URL);

// Initialize result
const seen = new Set();
const updatedItems = [
  { json: { links: `${BASE_URL}/` } }
];
seen.add(`${BASE_URL}/`);

items.forEach((item) => {
  const link = item?.json?.links;
  if (typeof link !== "string") return;

  const cleanLink = link.trim().toLowerCase();

  const isAnchor = cleanLink.startsWith("#");
  const isMailOrPhone = cleanLink.startsWith("mailto:") || cleanLink.startsWith("tel:");
  const isEmpty = cleanLink === "" || cleanLink === "#" || cleanLink === "#.";
  const isSocial = [
    "facebook.com", "instagram.com", "linkedin.com", "twitter.com",
    "youtube.com", "tiktok.com", "pinterest.com", "snapchat.com"
  ].some(domain => cleanLink.includes(domain));

  if (isAnchor || isMailOrPhone || isEmpty || isSocial) return;

  let fullUrl = null;

  if (cleanLink.startsWith("http://") || cleanLink.startsWith("https://")) {
    const linkHostname = getHostname(cleanLink);
    if (linkHostname !== baseHostname) return;

    // Extract path manually
    const pathParts = cleanLink.split(linkHostname);
    const path = pathParts[1]?.split(/[?#]/)[0] || "/";
    const cleanPath = path.endsWith("/") && path !== "/" ? path.slice(0, -1) : path;

    fullUrl = `${BASE_URL}${cleanPath}`;
  } else if (cleanLink.startsWith("/")) {
    const cleanPath = cleanLink.endsWith("/") && cleanLink !== "/" ? cleanLink.slice(0, -1) : cleanLink;
    fullUrl = `${BASE_URL}${cleanPath}`;
  }

  if (fullUrl && !seen.has(fullUrl)) {
    seen.add(fullUrl);
    updatedItems.push({ json: { links: fullUrl } });
  }
});

return updatedItems;
